var appUrl = req.body.propertyList.eka_connect_host + '/meta/app';
var app = {
  method: 'GET',
  url: appUrl,
  headers: {
    'Content-Type': 'application/json',
    Authorization: req.headers.authorization,
    'X-TenantID': req.headers['x-tenantid']
  },
  json: true
};
function get_app() {
  return new Promise(function(resolve, reject) {
    request(app, function(err, response, body) {
      if (err || body.error) reject(body);
      else {
        resolve(body);
      }
    });
  });
}
var seeded_apps = {
  method: 'GET',
  url:
    req.body.propertyList.platform_url +
    '/spring/apps?_dc=1589541458058&apptype=Pre-packaged%20Apps&isAdminContext=&includeSeededApps=true',
  headers: {
    Authorization: req.headers.authorization
  },
  json: true
};
function get_seeded_apps() {
  return new Promise(function(resolve, reject) {
    request(seeded_apps, function(err, response, body) {
      if (err || body.error) reject(body);
      else {
        resolve(body);
      }
    });
  });
}
Promise.all([get_seeded_apps(), get_app()]).then(result => {
  var platform_apps = result[0];
  var connect_apps = result[1];
  var id = [];
  var data = [];
  platform_apps.forEach(el => {
    id.push(el['_id']);
  });
  connect_apps.forEach(el => {
    if (id.indexOf(el.platform_id) > -1) {
      data.push(el);
    }
  });
  res.status(200).send(data);
