var app_uuid = req.body.selectedData.app_uuid;
    var platform = {
      method: 'GET',
      url: req.body.propertyList.eka_connect_host + '/property/platform_url',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        username: 'e-bolprpa',
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      json: true
    };

    function get_platform() {
      return new Promise(function(resolve, reject) {
        console.log('Request - ' + platform.url);
        console.log('Payload -' + JSON.stringify(platform));
        request(platform, function(err, response, body) {
          console.log('Request Body - ' + JSON.stringify(body));
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }

    var object = {
      method: 'GET',
      url: req.body.propertyList.eka_connect_host + '/meta/object',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        username: 'e-bolprpa',
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      json: true
    };

    function get_object() {
      return new Promise(function(resolve, reject) {
        console.log('Request - ' + object.url);
        console.log('Payload -' + JSON.stringify(object));
        request(object, function(err, response, body) {
          console.log('Request Body - ' + JSON.stringify(body));
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }
    var policy = {
      method: 'GET',
      url: req.body.propertyList.eka_connect_host + '/policy/' + app_uuid + '/role-grants',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        username: 'e-bolprpa',
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      json: true
    };

    function get_policy() {
      return new Promise(function(resolve, reject) {
        console.log('Request - ' + policy.url);
        console.log('Payload -' + JSON.stringify(policy));
        request(policy, function(err, response, body) {
          console.log('Request Body - ' + JSON.stringify(body));
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }

    var roles = {
      method: 'GET',
      url: '/spring/smartapp/getRolesList',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        username: 'e-bolprpa',
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      json: true
    };
    function get_roles(platform_url) {
      return new Promise(function(resolve, reject) {
        roles.url = platform_url + roles.url;
        console.log('Request - ' + roles.url);
        console.log('Payload -' + JSON.stringify(roles));
        request(roles, function(err, response, body) {
          console.log('Request Body - ' + JSON.stringify(body));
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }

    get_platform()
      .then(result => {
        var data = {};
        var platform_url = result.propertyValue;
        Promise.all([get_roles(platform_url), get_object(), get_policy()])
          .then(resp => {
              var auth_users = {
                "clientId": 8,
                "id": "AUTHENTICATED_USERS",
                "name": "All Users"
            };
            data.roles = resp[0];
            data.roles.data.push(auth_users);
            data.object = resp[1];
            data.grants = resp[2];
            console.log('Data - ' + JSON.stringify(data));
            res.status(200).send(data);
          })
          .catch(err => {
            console.log('Error Executing Processor -' + err);
            res.status(500).send(err);
          });
      })
      .catch(err => {
        console.log('Error Executing Processor -' + err);
        res.status(500).send(err);
      });
