    req.body.mdmProcessorServiceKey = JSON.parse(req.body.mdmProcessorServiceKey);
    function findindex(serviceKey) {
      return req.body.mdmProcessorServiceKey.findIndex(x => x.serviceKey == serviceKey);
    }
    var countryPartyRequest = {
      method: 'POST',
      url: req.body.propertyList.platform_url + '/mdmapi/masterdatas/cpDetails',
      headers: {
        Authorization: req.headers.authorization,
        'Content-Type': 'application/json'
      },
      json: true
    };
    var get_counterParty = function get_counterParty() {
      return new Promise(function(resolve, reject) {
        request(countryPartyRequest, function(err, response, body) {
          if (response.statusCode >= 200 && response.statusCode <= 299) {
            resolve(body);
          } else {
            reject(body);
          }
        });
      });
    };
    var contractRefNo = {
      method: 'GET',
      url: req.body.propertyList.platform_url + '/api/contract/getContractItemValues/',
      headers: {
        Authorization: req.headers.authorization,
        'Content-Type': 'application/json'
      },
      json: true
    };
    var get_contractRefNo = function get_contractRefNo() {
      return new Promise(function(resolve, reject) {
        request(contractRefNo, function(err, response, body) {
          if (response.statusCode >= 200 && response.statusCode <= 299) {
            resolve(body);
          } else {
            reject(body);
          }
        });
      });
    };
    var gmrId = {
      method: 'POST',
      url: req.body.propertyList.platform_url + '/api/contract/getGmrDetailsByItemRefNoForCollateral',
      headers: {
        Authorization: req.headers.authorization,
        'Content-Type': 'application/json'
      },
      body: { internalContractItemRefNo: '' },
      json: true
    };
    var get_gmrId = function get_gmrId() {
      return new Promise(function(resolve, reject) {
        request(gmrId, function(err, response, body) {
          if (response.statusCode >= 200 && response.statusCode <= 299) {
            resolve(body);
          } else {
            reject(body);
          }
        });
      });
    };
    var InvoiceId = {
      method: 'POST',
      url: req.body.propertyList.platform_url + '/api/contract/getInvoiceDetailsbyGmrAndContract',
      body: {
        internalContractItemRefNo: '',
        internalGmrRefNo: ''
      },
      headers: {
        Authorization: req.headers.authorization,
        'Content-Type': 'application/json'
      },
      json: true
    };
    var get_InvoiceId = function get_InvoiceId() {
      return new Promise(function(resolve, reject) {
        request(InvoiceId, function(err, response, body) {
          if (response.statusCode >= 200 && response.statusCode <= 299) {
            resolve(body);
          } else {
            reject(body);
          }
        });
      });
    };
    async function main() {
      let mdmdata = {};

      if (findindex('contractRefNoKey') != -1) {
        contractRefNo.url =
          contractRefNo.url + req.body.mdmProcessorServiceKey[findindex('contractRefNoKey')].dependsOn[0];
        let contractRef = await get_contractRefNo();
        mdmdata['contractRefNoKey'] = JSON.parse(contractRef.description).map(item => {
          return {
            key: item.internalContractItemRefNo,
            value: item.contractItemRefNo
          };
        });
      }
      if (findindex('gmrIdKey') != -1) {
        var dependsOn = req.body.mdmProcessorServiceKey[findindex('gmrIdKey')].dependsOn;
        gmrId.body.internalContractItemRefNo = dependsOn[0];
        let gmrRefNo = await get_gmrId();
        mdmdata['gmrIdKey'] = JSON.parse(gmrRefNo.description).map(item => {
          return {
            key: item.internalGmrRefNo,
            value: item.gmrRefNo
          };
        });
      }
      if (findindex('counterpartyKey') != -1) {
        let counterPartyData = await get_counterParty();
        mdmdata['counterpartyKey'] = counterPartyData.map(item => {
          return { key: item.profileId, value: item.companyname };
        });
      }
      if (findindex('invoiceIdKey') != -1) {
        var dependsOn = req.body.mdmProcessorServiceKey[findindex('invoiceIdKey')].dependsOn;
        InvoiceId.body.internalContractItemRefNo = dependsOn[0];
        InvoiceId.body.internalGmrRefNo = dependsOn[1];
        let invoiceIdKey = await get_InvoiceId();
        mdmdata['invoiceIdKey'] = JSON.parse(invoiceIdKey.description).map(item => {
          return {
            key: item.internalInvoiceRefno,
            value: item.invoiceRefno
          };
        });
      }
      return mdmdata;
    }
    main().then(mdmdata => {
      res.status(200).send(mdmdata);
    });
