    var app_uuid = req.body.selectedData.app_uuid;
    var tenant = {
      method: 'GET',
      url: req.body.propertyList.eka_connect_host + '/property',
      headers: {
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      json: true
    };

    var app = {
      method: 'GET',
      url: req.body.propertyList.eka_connect_host + '/property/' + app_uuid + '/all',
      headers: {
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      json: true
    };

    function get_tenant() {
      return new Promise(function(resolve, reject) {
        console.log('Request - ' + tenant.url);
        console.log('Payload -' + JSON.stringify(tenant));
        request(tenant, function(err, response, body) {
          console.log('Request Body - ' + JSON.stringify(body));
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }

    function get_app() {
      return new Promise(function(resolve, reject) {
        console.log('Request - ' + app.url);
        console.log('Payload -' + JSON.stringify(app));
        request(app, function(err, response, body) {
          console.log('Request Body - ' + JSON.stringify(body));
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }

    if (app_uuid) {
      Promise.all([get_tenant(), get_app()])
        .then(result => {
          var data = {};
          data.tenant = result[0];
          data.app = result[1];
          console.log('Response - ' + JSON.stringify(data));
          res.status(200).send(data);
        })
        .catch(err => {
          console.log('Error Executing Processor -' + err);
          res.status(500).send(err);
        });
    } else {
      get_tenant()
        .then(result => {
          var data = {};
          data.tenant = result;
          console.log('Response - ' + JSON.stringify(data));
          res.status(200).send(data);
        })
        .catch(err => {
          console.log('Error Executing Processor -' + err);
          res.status(500).send(err);
        });
    }
