req.body;
var platform = {
  method: 'GET',
  url: req.body.propertyList.platform_url + '/spring/permcodes/-1',
  headers: {
    Authorization: req.headers.authorization
  },
  json: true
};
function get_platform() {
  return new Promise(function(resolve, reject) {
    console.log('Request - ' + platform.url);
    console.log('Payload -' + JSON.stringify(platform));
    request(platform, function(err, response, body) {
      console.log('Request Body - ' + JSON.stringify(body));
      if (err || body.error) reject(body);
      else {
        resolve(body);
      }
    });
  });
}
get_platform()
  .then(permCodes => {
    var codes = permCodes.permCodes;
    var data = {};
    if (req.body.propertyList.tenant_level_tabs.indexOf('*') > -1) {
      if (codes.indexOf('ADMIN_WORKFLOW_OBJECTS_VIEW') > -1) data.objectView = true;
      if (codes.indexOf('ADMIN_WORKFLOW_LIST_OF_APPS_VIEW') > -1) data.appView = true;
      if (codes.indexOf('ADMIN_WORKFLOW_OBJECTS_VIEW') > -1) data.propertyView = true;
      if (codes.indexOf('ADMIN_WORKFLOW_OBJECTS_CREATE') > -1) data.objectCreate = true;
      if (codes.indexOf('ADMIN_WORKFLOW_OBJECTS_EDIT') > -1) data.objectEdit = true;
    } else {
      if (
        codes.indexOf('ADMIN_WORKFLOW_OBJECTS_VIEW') > -1 &&
        req.body.propertyList.tenant_level_tabs.indexOf('OBJECT') > -1
      )
        data.objectView = true;
      if (
        codes.indexOf('ADMIN_WORKFLOW_LIST_OF_APPS_VIEW') > -1 &&
        req.body.propertyList.tenant_level_tabs.indexOf('APP') > -1
      )
        data.appView = true;
      if (
        codes.indexOf('ADMIN_WORKFLOW_OBJECTS_VIEW') > -1 &&
        req.body.propertyList.tenant_level_tabs.indexOf('PROPERTY') > -1
      )
        data.propertyView = true;
      if (
        codes.indexOf('ADMIN_WORKFLOW_OBJECTS_CREATE') > -1 &&
        req.body.propertyList.tenant_level_tabs.indexOf('OBJECT') > -1
      )
        data.objectCreate = true;
      if (
        codes.indexOf('ADMIN_WORKFLOW_OBJECTS_EDIT') > -1 &&
        req.body.propertyList.tenant_level_tabs.indexOf('OBJECT') > -1
      )
        data.objectEdit = true;
    }
    console.log('Response - ' + JSON.stringify(data));
    res.status(200).send(data);
  })
  .catch(err => {
    console.log('Error Executing Processor -' + err);
    res.status(500).send(err);
  });
