var selectedData = req.body;
var contractType;
if (req.body.contractItemRefNo && req.body.contractItemRefNo.charAt(0) == "P")
  contractType = "Purchase";
else if (
  req.body.contractItemRefNo &&
  req.body.contractItemRefNo.charAt(0) == "S"
)
  contractType = "Sale";
var mandatoryDataBody = {
  attributes: [
    {
      attributeName: "Contract Type",
      attributeValue: contractType,
    },
    {
      attributeName: "Contract Incoterm",
      attributeValue: req.body.contractIncoTerm,
    },
    {
      attributeName: "Valuation Incoterm",
      attributeValue: req.body.valuationIncoTerm,
    },
    {
      attributeName: "Payment Term",
      attributeValue: req.body.paymentTerm,
    },
  ],
};

var mandatoryData = {
  method: "POST",
  url: req.body.propertyList.eka_utility_host + "/costapp/getCostComponents",
  headers: {
    Authorization: req.headers.authorization,
    "X-TenantID": req.headers["x-tenantid"],
  },
  body: mandatoryDataBody,
  json: true,
};
function get_mandatoryData() {
  return new Promise(function (resolve, reject) {
    request(mandatoryData, function (err, response, body) {
      console.log("Request:" + mandatoryData);

      if (err || body.error) reject(body);
      else {
        resolve(body);
      }
    });
  });
}

function confirmDelete(templateName) {
  var value = true;
  return new Promise((resolve, reject) => {
    get_mandatoryData().then((result) => {
      for (var i = 0; i < result.length; i++) {
        if (result[i].templateName == templateName) {
          value = false;
          break;
        }
      }
      resolve(value);
    });
  });
}

if (selectedData.costType == "template" && selectedData.templateName) {
  confirmDelete(selectedData.templateName).then((value) => {
    if (value) {
      res.status(200).send(selectedData);
    } else {
      res
        .status(500)
        .send(
          "Mandatory costs estimates are defaulted from rule setup and cannot be deleted. To remove this estimate, please remove this estimate template from rule setup."
        );
    }
  });
} else if (selectedData.costType == "estimate") {
  res.status(200).send(selectedData);
} else {
  res.status(200).send(selectedData);
}
