var corporate = {
  method: "GET",
  url:
    req.body.propertyList.eka_mdm_host +
    "/mdm/d33143ac-4164-4a3f-8d30-61d845c9eeed/corporateInfo",
  headers: {
    Authorization: req.headers.authorization,
    "X-TenantID": req.headers["x-tenantid"],
    "Content-Type": "application/json",
  },
  json: true,
};

function get_corporate() {
  return new Promise(function (resolve, reject) {
    console.log("Request - " + corporate.url);
    console.log("Payload -" + JSON.stringify(corporate));
    request(corporate, function (err, response, body) {
      console.log("Request Body - " + JSON.stringify(body));
      if (err || body.error) reject(body);
      else {
        resolve(body);
      }
    });
  });
}

if (req.body.queryParams) {
