   const connect = {
      method: 'POST',
      url: req.body.propertyList.eka_connect_host + `/workflow/data/`,
      headers: {
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      body: {
        appId: '12325a98-a959-4939-9005-4158d136afcd',
        workFlowTask: 'accountqueryElastic',
        qP: {
          from: 0,
          size: 10000
        }
      },
      json: true
    };

    function get_elasticData() {
      return new Promise(function(resolve, reject) {
        request(connect, function(error, response, body) {
          if (response.statusCode >= 200 && response.statusCode <= 299) {
            resolve(body);
          } else {
            reject(body);
          }
        });
      });
    }

    get_elasticData()
      .then(records => {
        let result = records.data.filter(i => {
          var val = i.type.toLowerCase();
          if (i.userType.toLowerCase() === 'grower') {
            i.accountStatusAll = 'Statement';
            if (i.type.toLowerCase() === 'accounts payables') {
              i.accountStatus = 'Payables';
            }
            if (i.type.toLowerCase() === 'accounts receivables') {
              i.accountStatus = 'Receivables';
            }
          }
          return i;
        });
        res.status(200).send(result);
      })
      .catch(function(err) {
        return res.status(500).send(err);
      });
