const serviceKey = JSON.parse(req.body.mdmProcessorServiceKey)[0].serviceKey;
const userInfo = req.body.propertyList.eka_connect_host + '/api/getbpuserinfo';
const appId = req.body.appId;
const tenant = req.headers['X-TenantID'];
const taskId = req.body.taskId;

const financeMasterData = {
  appId: appId,
  objectId: 'd59b8c10-8c94-4b87-a430-f6fd69888a37',
  collectionName: 'Finance Master Data',
  tenant: tenant
};

const subGroupMaster = {
  appId: appId,
  objectId: 'd59b8c10-8c94-4b87-a430-f6fd69888a37' /* not at all relevant object id */,
  collectionName: 'BP Sub Group Setup',
  tenant: tenant
};

const getData = url =>
  new Promise(function(resolve, reject) {
    request(
      {
        method: 'GET',
        url,
        headers: {
          Authorization: req.headers.authorization
        },
        json: true
      },
      function(err, response, body) {
        if (response && response.statusCode >= 200 && response.statusCode <= 299) {
          resolve(body);
        } else {
          reject(body);
        }
      }
    );
  });

const getCollectionMapper = config =>
  new Promise((resolve, reject) => {
    const r = {
      method: 'POST',
      url: `${req.body.propertyList.eka_connect_host}/collectionmapper/${config.appId}/${
        config.objectId
      }/fetchCollectionRecords`,
      headers: {
        Authorization: req.headers.authorization,
        ttl: 600,
        'X-TenantID': config.tenant,
        'Content-Type': 'application/json'
      },
      body: {
        collectionName: config.collectionName
      },
      json: true
    };
    request(r, (err, response, body) => {
      if (response && response.statusCode >= 200 && response.statusCode <= 299) {
        resolve(body);
      } else {
        reject(body);
      }
    });
  });

function formatData(n) {
  return n
    .toString()
    .split(/(?=(?:\d{3})+(?:\.|$))/g)
    .join(' ');
}

const objSorter = prop => (obj1, obj2) => {
  if (obj1[prop] < obj2[prop]) {
    return -1;
  }
  if (obj1[prop] > obj2[prop]) {
    return 1;
  }
  return 0;
};

const getSubGroup = (list, format, code) =>
  list
    .reduce((acc, bp) => {
      let groups;

      if (code) {
        groups = (bp.subGroup || [])
          .filter(i => i.code && i.code.toLowerCase() == code.toLowerCase())
          .map(item => ({
            key: item.refNo,
            value: `${bp.name}\n${format ? formatData(bp.refNo) : bp.refNo}\n${item.name}`
          }));
      } else {
        groups = [
          {
            key: bp.refNo,
            value: `${bp.name}\n${format ? formatData(bp.refNo) : bp.refNo}`
          }
        ];
      }

      acc.push(...groups);
      return acc;
    }, [])
    .sort(objSorter('value'));

const subGroupCode = userDetails =>
  new Promise((resolve, reject) =>
    getCollectionMapper(subGroupMaster)
      .then(result => {
        if (result && Array.isArray(result)) {
          const config = result.find(i => i.Workflow == taskId && i.UserType == userDetails.customerType && i.SubGroupApplicable.toLowerCase() == 'y');

          if (config) {
            resolve(config.SubGroupCode);
          } else {
            console.log(
              `Sub group configuration not found for ${taskId} and ${
                userDetails.customerType
              }. Defaulting to not applicable.`
            );
            resolve(null);
          }
        } else {
          reject('Sub group configuration is missing');
        }
      })
      .catch(err => {
        reject(err);
      })
  );

const bpList = new Promise((resolve, reject) => {
  getData(userInfo)
    .then(t => {
      const userDetails = t.data;
      if (userDetails.BusinessPartyDetails) {
        subGroupCode(userDetails)
          .then(code => {
            resolve(getSubGroup(userDetails.BusinessPartyDetails, userDetails.customerType === 'Demand Customer', code));
          })
          .catch(err => {
            reject(err);
          });
      } else {
        reject('BusinessPartyDetails missing in user details.');
      }
    })
    .catch(err => {
      reject(err);
    });
});

const financialYears = new Promise((resolve, reject) => {
  getCollectionMapper(financeMasterData)
    .then(result => {
      const data = result.map(i => ({
        monthAndYear: moment(i.Month).format('MMM-YY'),
        financialYr: i['Financial Year and Quarter'],
        month: i['Financial Month']
      }));
      const today = moment().format('MMM-YY');
      const currentFY = data.find(i => i.monthAndYear === today).financialYr;

      const financialYears = Array.from(new Set(data.map(i => i.financialYr)));
      const monthAndYears = data.reduce((acc, curr) => {
        if (!acc[curr.financialYr]) {
          acc[curr.financialYr] = [];
        }
        acc[curr.financialYr].push(curr.month);
        return acc;
      }, {});

      const index = financialYears.findIndex(i => i === currentFY);

      const filters = {
        periods: {
          'Last 4 quarters': financialYears.slice(index - 3, index + 1).reverse(),
          'Last 8 quarters': financialYears.slice(index - 7, index + 1).reverse()
        }
      };

      filters.months = filters.periods['Last 8 quarters'].reduce((acc, curr) => {
        acc[curr] = monthAndYears[curr];
        return acc;
      }, {});

      filters.financialYears = Object.keys(filters.periods).reduce((acc, curr) => {
        filters.periods[curr].forEach(i => {
          if (!acc[i]) {
            acc[i] = [];
          }
          acc[i].push(curr);
        });
        return acc;
      }, {});
      resolve(filters);
    })
    .catch(err => {
      reject(err);
    });
});

Promise.all([bpList, financialYears])
  .then(data => {
    res.status(200).send({
      [serviceKey]: data[0],
      financeFilters: [data[1]]
    });
  })
  .catch(err => {
    console.error(err);
    res.status(200).send({
      [serviceKey]: [],
      financeFilters: [
        {
          periods: {
            'Last 4 quarters': [],
            'Last 8 quarters': []
          },
          financialYears: {},
          months: {}
        }
      ]
    });
  });
