    var connectData = {
      method: "GET",
      url:
        req.body.propertyList.eka_connect_host +
        "/data/12325a98-a959-4939-9005-4158d136afcd/0b2d91ec-e3e8-45e2-9bb6-5e0d17cd499b?movementRefNo=",
      headers: {
        Authorization: req.headers.authorization,
        "X-TenantID": req.headers["x-tenantid"],
      },
      json: true,
    };
    function get_connectData() {
      return new Promise(function (resolve, reject) {
        request(connectData, function (err, response, body) {
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }

    let finalData = [];
    let data = req.body.bulkPayLoadData;
    console.log(data);
    if (data) {
      connectData.url =
        req.body.propertyList.eka_connect_host +
        "/data/12325a98-a959-4939-9005-4158d136afcd/0b2d91ec-e3e8-45e2-9bb6-5e0d17cd499b?movementRefNo=" +
        data[0].movementRefNo;
      get_connectData()
        .then(async (result) => {
          data[0].sys__data__state = "Delete";
          if (result.length) {
            data[0]["id"] = result[0]["_id"];
            finalData.push(data[0]);
            for (let i = 1; i < data.length; i++) {
              connectData.url =
                req.body.propertyList.eka_connect_host +
                "/data/12325a98-a959-4939-9005-4158d136afcd/0b2d91ec-e3e8-45e2-9bb6-5e0d17cd499b?movementRefNo=" +
                data[i].movementRefNo;
              await get_connectData()
                .then((record) => {
                  data[i].sys__data__state = "Delete";
                  data[i]["id"] = record[0]["_id"];
                  finalData.push(data[i]);
                })
                .catch((err) => {
                  res.status(500).send(err);
                });
            }
            res.status(200).send(finalData);
          }
          else {
            res.status(500).send("No Records Found");
          }
        })
        .catch((err) => {
          res.status(500).send(err);
        });
    }
