const sendError = (message, context) =>
    res.status(400).send({
        errorCode: '004',
        errorMessage: message,
        errorContext: null,
        errorLocalizedMessage: message,
        errors: [
            {
                errorCode: '004',
                errorMessage: message,
                errorContext: context,
                errorLocalizedMessage: message,
                errors: null,
            },
        ],
    });
if (!req.body.offerbpdetails) {
    return sendError('Please choose an bpRefNo', '{entercontracts:offerbpdetails}');
}

const bpDetails = req.body.offerbpdetails.split(' - ');
const data = (req.body.offercontracts || []).reduce((ac1, cur1, ind1) => {
  
    ac1.push({
        requestType: 'New contract',
        product: cur1.offerproduct,
        quality: cur1.offerquality,
        cropYear:  cur1.offerseason === '__empty__' ? '' :cur1.offerseason,
        contractItemQuantity: cur1.offerquantity,
        contractPrice: cur1.offerprice,
        dischargeLocationCity: cur1.offerregion,
        INCOTerm: cur1.offerdeliverterm,
        offerdeliveryperiod : cur1.offerdeliveryperiod,
        shipmentPeriodFrom: cur1.offerdeliveryperiod == '' ? null: moment.utc(cur1.offerdeliveryperiod).startOf('month').format('YYYY-MM-DD'),
        shipmentPeriodTo: cur1.offerdeliveryperiod == '' ? null : moment.utc(cur1.offerdeliveryperiod).endOf('month').format('YYYY-MM-DD'),
        offerExpiryDate: cur1.offerexpiry,
        offersite : cur1.offersite,
        offersustainable: cur1.offersustainable === '__empty__'? '' :cur1.offersustainable,
        contractType: cur1.offercontracttype === '__empty__' ? '' :cur1.offercontracttype,
        BPRefNo: bpDetails[0],
        counterPartyName: bpDetails[1],
        _groupNo: ind1
    });
    if(cur1.offerdeliverterm != 'DB'){
      if(!ac1[ind1].shipmentPeriodFrom) ac1[ind1].shipmentPeriodFrom = moment.utc().add(1, 'M').startOf('month').format("YYYY-MM-DD");
      if(!ac1[ind1].shipmentPeriodTo) ac1[ind1].shipmentPeriodTo = moment.utc().add(1, 'M').endOf('month').format("YYYY-MM-DD");
    } 
    return ac1;
}, []);
console.log(data);
res.status(200).send(data);
