  var intGmrRefNo = req.body.selectedData['internalGmrRefNo'];
    var gmrRefNo = req.body.selectedData['gmrRefNo'];
    var collection = {
      method: 'POST',
      url: req.body.propertyList.eka_supplierconnect_host + '/collection/assayAndStockList',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        username: 'e-bolprpa',
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      body: {
        intGmrRefNo: intGmrRefNo,
        gmrRefNo: gmrRefNo
      },
      json: true
    };
    function get_collection() {
      return new Promise(function (resolve, reject) {
        console.log('Request - ' + collection.url);
        console.log('Payload -' + JSON.stringify(collection));
        request(collection, function (err, response, body) {
          console.log('Request Body - ' + JSON.stringify(body));
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }

    function get_viewgmr() {
      return new Promise(function (resolve, reject) {
        console.log("Request - " + viewgmr.url);
        console.log("Payload -" + JSON.stringify(viewgmr));
        request(viewgmr, function (err, response, body) {
          console.log("Request Body - " + JSON.stringify(body));
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }
    var viewgmr = {
      method: "POST",
      url: req.body.propertyList.eka_connect_host + "/workflow",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: req.headers.authorization,
        "X-TenantID": req.headers["x-tenantid"],
      },
      body: {
        appId: "7e484cae-36aa-46dc-b9ae-33c8d246f056",
        workflowTaskName: "viewgmrdata_SC",
        task: "viewgmrdata_SC",
        payLoadData: "",
        output: {
          ['viewgmrdata_SC']: {
            ['intGmrRefNo']: intGmrRefNo,
          },
        },
      },
      json: true,
    };

    Promise.all([
      get_collection(),
      get_viewgmr(),
    ])
      .then(function (result) {

        if (result[0] && !Array.isArray(result[0]['Provisional Assay'])) {
          if (result[0] && Array.isArray(result[0]['Shipment Assay'])) {
            result[0]['Provisional Assay'] = result['Shipment Assay'];
          }
        }
        let allAssayData = result[0];

        var data = result[0]['Provisional Assay'][0].stockList;
        for (var i = 0; i < data.length; i++) {
          let remarks = data[i].remarks;
          let remarksArr;
          if (remarks) {
            remarksArr = remarks.split(',');
            for (let j = 0; j < remarksArr.length; j++) {
              let prop = remarksArr[j].split('-');
              data[i][prop[0]] = prop[1];
            }
          }
          data[i].drums = parseInt(data[i].Drums);
          data[i].bags = parseInt(data[i].Bags);
          data[i].boxes = parseInt(data[i].Boxes);
          data[i].pallets = parseInt(data[i].Pallets);
          data[i].totalPackage = parseInt(data[i]['noOfBags']);
          data[i].viewGmrData = result[1];
        }
        data.forEach((item) => {
          item['allAssayData'] = JSON.parse(JSON.stringify(allAssayData));
        })
        console.log('Response - ' + (data));

        res.status(200).send(data);
      })
      .catch(err => {
        console.log('Error Executing Processor -' + err);
        res.status(500).send(err);
      });
