    var shipment = {
      method: 'POST',
      url: req.body.propertyList.eka_ctrm_host + '/api/logistic/finalAssayListing',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: { contractRefNo: req.body.payLoadData.contractItem },
      json: true
    };
    function get_shipment() {
      return new Promise(function(resolve, reject) {
        console.log('Request - ' + shipment.url);
        request(shipment, function(err, response, body) {
          //console.log('Response - ' + body);
          if (err || body.error) reject(body);
          else {
            //console.log('Success');
            resolve(body);
          }
        });
      });
    }
    get_shipment().then(data => {
      if (data.length == 0) {
        var output = {
          appId: req.body.appId,
          flow: req.body.flow,
          object: req.body.object,
          objectMeta: req.body.objectMeta,
          properties: req.body.properties
        };
        res.status(200).send(output);
      } else {
        let elem = data[0].elementDetails.split(',');
        elem.sort();
        elem.forEach(item => {
          let elementlable = item.split('(')[0].trim();
          let elementHeader = item.split(':')[0].trim();

          req.body.objectMeta.fields[elementlable] = {
            [elementlable]: elementHeader,
            labelKey: elementlable,
            type: 'textbox',
            isRequired: false,
            dataType: 'String'
          };
          req.body.objectMeta.fields[elementlable + '_hidden'] = {
            [elementlable + '_hidden']: elementlable + '_hidden',
            labelKey: elementlable + '_hidden',
            type: 'textbox',
            isRequired: false,
            dataType: 'Number'
          };
          req.body.flow.finalassaylistdynamic.fields.push({
            filter: true,
            edit: true,
            sort: true,
            key: elementlable,
            sortColumn: elementlable + '_hidden',
            align: 'right'
          });
          req.body.flow.finalassaylistdynamic.fields.push({
            filter: true,
            hidden: true,
            edit: true,
            sort: true,
            key: elementlable + '_hidden'
          });
        });
        var output = {
          appId: req.body.appId,
          flow: req.body.flow,
          object: req.body.object,
          objectMeta: req.body.objectMeta,
          properties: req.body.properties
        };
        res.status(200).send(output);
      }
    });
