var moment = require("moment");
var result = {};
result.data = req.body.bulkPayLoadData;
Date.prototype.monthName = function () {
  return this.toUTCString().split(" ")[2];
};

for (var i = 0; i < result.data.length; i++) {
  if (result.data[i].portalCreatedUser) {
    result.data[i].createdBy = result.data[i].portalCreatedUser;
  }
  //Added to sort based on GMR Ref
  result.data[i].gmrRefNo_hidden = result.data[i].gmrRefNo.split("-")[1];

  //Added for contract delivery sort
  let item = result.data[i].deliveryItemRefNo.split("-");
  item[3] = parseInt(item[3].toString().replace(".", ""), 10);
  result.data[i].deliveryItemRefNo_hidden = parseFloat(item[1] + '.' +item[3]);

  //Added for concatening columns in Destination
  result.data[i].incoLocation_concat =
    result.data[i].dischargeCity + "," + result.data[i].dischargeCountry;

  if (result.data[i].eta) {
    result.data[i].eta = result.data[i].eta
      ? result.data[i].eta.substring(0, 10)
      : null;
    result.data[i].shipmentdate = result.data[i].shipmentdate
      ? result.data[i].shipmentdate.substring(0, 10)
      : null;
    result.data[i].blDate = result.data[i].blDate
      ? result.data[i].blDate.substring(0, 10)
      : null;
    result.data[i].gmrCreatedDate = result.data[i].gmrCreatedDate
      ? result.data[i].gmrCreatedDate.substring(0, 10)
      : null;
    result.data[i].landingDate = result.data[i].landingDate
      ? result.data[i].landingDate.substring(0, 10)
      : null;
    result.data[i].invoiceDueDate = result.data[i].invoiceDueDate
      ? result.data[i].invoiceDueDate.substring(0, 10)
      : null;
    result.data[i].gmrDueDate = result.data[i].gmrDueDate
      ? result.data[i].gmrDueDate.substring(0, 10)
      : null;
  }

  //SC-1827 - Provision to search Adviced Deliveries
  if (result.data[i].shipmentdate) {
    var today = moment().format("YYYY-MM-DD");
    var shipmentDate = moment(result.data[i].shipmentdate, "YYYY-MM-DD");
    var isLoaded = shipmentDate.isSameOrAfter(today);
    result.data[i].isShipmentLoaded = isLoaded ? "Not Loaded" : "Loaded";
  }

  if (result.data[i].latestActivityBy === "Supplier Portal") {
    result.data[i].updatedBy = result.data[i].portalUpdatedUser;
  }

  if (result.data[i]["latestAssayType"]) {
    if (result.data[i]["latestAssayType"].toLowerCase() === "self assay")
      result.data[i].latestAssayType = "Boliden Assay";
    else if (
      result.data[i]["latestAssayType"].toLowerCase() === "counterparty assay"
    )
      result.data[i].latestAssayType = "Supplier Assay";
  }
  if (!result.data[i].landingDate || result.data[i].landingDate == null) {
    result.data[i]["assayDueDate"] = null;
  } else {
    let date = new Date(result.data[i]["landingDate"]);
    if (
      result.data[i]["product"] &&
      (result.data[i]["product"].toLowerCase() == "cu material" ||
        result.data[i]["product"].toLowerCase() == "pm material")
    ) {
      date.setDate(date.getDate() + 20);
    } else if (
      result.data[i]["qualityName"] &&
      (result.data[i]["qualityName"].toLowerCase() == "alloys coarse" ||
        result.data[i]["qualityName"].toLowerCase() == "alloys coarse iba" ||
        result.data[i]["qualityName"].toLowerCase() == "alloys fine" ||
        result.data[i]["qualityName"].toLowerCase() == "alloys fine iba" ||
        result.data[i]["qualityName"].toLowerCase() == "alloys ingots")
    ) {
      date.setDate(date.getDate() + 35);
    } else {
      date.setDate(date.getDate() + 42);
    }
    var dd = date.getDate();
    var mm = date.monthName();
    var y = date.getFullYear();
    if (dd < 10) dd = "0" + dd;
    if (mm < 10) mm = "0" + mm;
    var formattedDate = dd + "-" + mm + "-" + y;
    result.data[i]["assayDueDate"] = formattedDate;
  }
  if (result.data[i]["bolidenAssayAsFinal"] == "Y") {
    result.data[i]["bolidenAssayAsFinalDisplay"] = "Yes";
  } else {
    result.data[i]["bolidenAssayAsFinalDisplay"] = "No";
  }
  if (result.data[i]["markedForUmpire"] == "Y") {
    result.data[i]["markedForUmpireDisplay"] = "Yes";
  } else {
    result.data[i]["markedForUmpireDisplay"] = "No";
  }
  if (result.data[i]["latestAssayType"] == "Partial Self Assay") {
    result.data[i]["latestAssayType"] = "Partial Boliden Assay";
  }
}
console.log("Response length - " + result.data.length);
res.status(200).send(result);
