var moment = require("moment");
let mdmbody = JSON.parse(req.body.mdmProcessorServiceKey);
req.body.mdmProcessorServiceKey = JSON.parse(req.body.mdmProcessorServiceKey);

function findindex(serviceKey) {
  return req.body.mdmProcessorServiceKey.findIndex(
    (x) => x.serviceKey == serviceKey
  );
}
var ekaEntity =
  req.body.propertyList.platform_url + "/spring/cashManagement/entity";

var platformEntity = {
  method: "GET",
  url: ekaEntity,
  headers: {
    Authorization: req.headers.authorization,
    "Content-Type": "application/json",
    ttl: 300,
    "X-TenantID": req.headers["x-tenantid"],
    "X-Remote-User": "ekaApp",
  },
  json: true,
};
var get_platformEntity = function get_platformEntity() {
  return new Promise(function (resolve, reject) {
    request(platformEntity, function (err, response, body) {
      if (response.statusCode >= 200 && response.statusCode <= 299) {
        resolve(body);
      } else {
        reject(body);
      }
    });
  });
};
var TemplateUrl =
  req.body.propertyList.platform_url + "/collection/v1/internal/template/";

var templateBody = {
  method: "GET",
  url: TemplateUrl,
  headers: {
    Authorization: req.headers.authorization,
    "Content-Type": "application/json",
    ttl: 300,
    "X-TenantID": req.headers["x-tenantid"],
    "X-Remote-User": "ekaApp",
  },
  json: true,
};
var get_template = function get_template(name) {
  templateBody.url =
    req.body.propertyList.platform_url +
    "/collection/v1/internal/template/" +
    name +
    "?start=0&limit=10000";
  return new Promise(function (resolve, reject) {
    request(templateBody, function (err, response, body) {
      if (response.statusCode >= 200 && response.statusCode <= 299) {
        resolve(body);
      } else {
        reject(body);
      }
    });
  });
};
var ekaPlanSource =
  req.body.propertyList.platform_url +
  "/spring/liquidityManagement/liquidityPlan?status=SUBMITTED";

var platformEkaPlanSource = {
  method: "GET",
  url: ekaPlanSource,
  body: "",
  headers: {
    Authorization: req.headers.authorization,
    "Content-Type": "application/json",
    ttl: 300,
    "X-TenantID": req.headers["x-tenantid"],
    "X-Remote-User": "ekaApp",
  },
  json: true,
};
var get_platformEkaPlanSource = function get_platformEkaPlanSource(
  entityIdss,
  planStartDate,
  planEndDate
) {
  platformEkaPlanSource.body = {
    asOfDate: planStartDate,
    toDate: planEndDate,
    clientID: "8",
    entityIds: [entityIdss],
  };
  return new Promise(function (resolve, reject) {
    request(platformEkaPlanSource, function (err, response, body) {
      if (response.statusCode >= 200 && response.statusCode <= 299) {
        resolve(body);
      } else {
        reject(body);
      }
    });
  });
};
async function main() {
  let mdmdata = {};
  let { datefilters } = req.body.selectedData;
  mdmdata["PlanFrequency"] = [
    { key: "Daily", value: "Daily" },
    { key: "Weekly", value: "Weekly" },
    { key: "Monthly", value: "Monthly" },
    { key: "Yearly", value: "Yearly" },
  ];
  mdmdata["PlanRollOver"] = [
    { key: "Yes", value: "Yes" },
    { key: "No", value: "No" },
  ];

  if (findindex("ngrFilterKey") != -1) {
    try {
      let start = new Date();
      if (datefilters && datefilters.hasOwnProperty("formcheckbox")) {
        let Entity = datefilters["formcheckbox"];
        mdmdata["ngrFilterKey"] = Entity.map((res) => {
          return { key: res, value: res };
        });
      } else {
        let Entity = await get_platformEntity();
        console.log("Entity", new Date() - start);
        mdmdata["ngrFilterKey"] = Entity.map((res) => {
          return { key: res["c1"], value: res["c1"] };
        });
      }
    } catch (error) {
      console.log(error);
    }
  }
  if (findindex("classification") != -1) {
    try {
      let start = new Date();
      let classification = await get_template("TransactionTypes");
      console.log("template", new Date() - start);
      mdmdata["classification"] = [
        ...new Set(classification.data.map((obj) => obj["Transaction Type"])),
      ].map((item) => {
        return { key: item, value: item };
      });
    } catch (error) {
      console.log(error);
    }
  }
  if (findindex("bankAccount") != -1) {
    try {
      let start = new Date();
      let bankAccount = await get_template("TreasuryBankAccount");
      let ent = mdmbody[findindex("bankAccount")].dependsOn[0];
      console.log("bankAccont", new Date() - start);
      mdmdata["bankAccount"] = bankAccount.data
        .filter((res) => res.Entity == ent)
        .map((item) => {
          return { key: item["Account Number"], value: item["Account Number"] };
        });
    } catch (error) {
      console.log(error);
    }
  }
  if (findindex("currency") != -1) {
    try {
      let start = new Date();
      let BankDetails = await get_template("TreasuryBankAccount");
      let ent = mdmbody[findindex("currency")].dependsOn[0];
      console.log("Currency Unit", new Date() - start);
      let filtered = BankDetails.data.filter((res) => res.Entity == ent);
      mdmdata["currency"] = [
        ...new Set(filtered.map((obj) => obj["Currency"])),
      ].map((item) => {
        return { key: item, value: item };
      });
    } catch (error) {
      console.log(error);
    }
  }
  if (
    findindex("bankName") != -1 &&
    findindex("bankId") != -1 &&
    findindex("currencyhidden") != -1
  ) {
    try {
      let start = new Date();
      let BankDetails = await get_template("TreasuryBankAccount");
      let ent = mdmbody[findindex("bankName")].dependsOn[0];
      let ActNo = mdmbody[findindex("bankName")].dependsOn[1];
      console.log("bank name", new Date() - start);
      let c = BankDetails.data.filter(
        (res) => res.Entity == ent && res["Account Number"] == ActNo
      )[0]["Currency"];
      mdmdata["currency"] = [{ key: c, value: c }];
      mdmdata["bankName"] = BankDetails.data.filter(
        (res) => res.Entity == ent && res["Account Number"] == ActNo
      )[0]["Bank Name"];
      mdmdata["bankId"] = BankDetails.data.filter(
        (res) => res.Entity == ent && res["Account Number"] == ActNo
      )[0]["Bank ID"];
      mdmdata["currencyhidden"] = BankDetails.data.filter(
        (res) => res.Entity == ent && res["Account Number"] == ActNo
      )[0]["Currency"];
    } catch (error) {
      console.log(error);
    }
  }
  if (findindex("planSource") != -1) {
    try {
      let planStartDate = moment(
        mdmbody[findindex("planSource")].dependsOn[0]
      ).format("YYYY-MM-DD");
      let planEndDate = moment(
        mdmbody[findindex("planSource")].dependsOn[1]
      ).format("YYYY-MM-DD");
      let entityIds = mdmbody[findindex("planSource")].dependsOn[2];
      let PlanSourceData = await get_platformEkaPlanSource(
        entityIds,
        planStartDate,
        planEndDate
      );
      mdmdata["planSource"] = [
        { label: "---Select-----", value: "---Select-----" },

        ...PlanSourceData.map((res) => {
          return { key: res["c2"], value: res["c2"] };
        }),
      ];
    } catch (error) {
      console.log(error);
    }
  }
  if (findindex("PlanFrequency") != -1) {
    try {
      let typeBusiness = mdmbody[findindex("PlanFrequency")].dependsOn[0];
      if (typeBusiness == "Scenario") {
        mdmdata["PlanFrequency"] = [
          { key: "Daily", value: "Daily" },
          { key: "Weekly", value: "Weekly" },
          { key: "Monthly", value: "Monthly" },
          { key: "Yearly", value: "Yearly" },
          { key: "Custom", value: "Custom" },
        ];
      } else {
        mdmdata["PlanFrequency"] = [
          { key: "Daily", value: "Daily" },
          { key: "Weekly", value: "Weekly" },
          { key: "Monthly", value: "Monthly" },
          { key: "Yearly", value: "Yearly" },
        ];
      }
    } catch (error) {
      console.log(error);
    }
  }
  if (findindex("PlanRollOver") != -1) {
    try {
      let typeBusiness = mdmbody[findindex("PlanRollOver")].dependsOn[0];
      if (typeBusiness == "Scenario") {
        mdmdata["PlanRollOver"] = [{ key: "No", value: "No" }];
      } else {
        mdmdata["PlanRollOver"] = [
          { key: "Yes", value: "Yes" },
          { key: "No", value: "No" },
        ];
      }
    } catch (error) {
      console.log(error);
    }
  }

  return mdmdata;
}
