var moment = require("moment");
let selected = req.body.hasOwnProperty("selectedData")
  ? req.body.selectedData
  : {};
let planactivitypayloadprep =
  selected.planactivitycreation.planactivity.planactivitycreation;

var get_platformEntityLiquidityPlanData =
  function get_platformEntityLiquidityPlanData() {
    return new Promise(function (resolve, reject) {
      let platformEntityLiquidityPlanData = {
        method: "GET",
        url:
          req.body.propertyList.platform_url +
          `/spring/liquidityManagement/liquidityPlan/${planactivitypayloadprep.liquidityPlanName}/planActivity?frequency=${planactivitypayloadprep.liquidityPlanPeriod}&fillExpenses=${req.body.selectedData.fillExpenses}`,
        body: {
          asOfDate: moment(planactivitypayloadprep.planStartDate).format(
            "YYYY-MM-DD"
          ),
          toDate: moment(planactivitypayloadprep.planEndDate).format(
            "YYYY-MM-DD"
          ),
          entityIds: [planactivitypayloadprep.entity],
        },
        headers: {
          Authorization: req.headers.authorization,
          "Content-Type": "application/json",
          "X-TenantID": req.headers["x-tenantid"],
        },
        json: true,
      };
      request(platformEntityLiquidityPlanData, function (err, response, body) {
        if (response.statusCode >= 200 && response.statusCode <= 299) {
          resolve(body);
        } else {
          reject(body);
        }
      });
    });
  };

get_platformEntityLiquidityPlanData()
