var connect = {
      method: 'GET',
      url: req.body.propertyList.eka_connect_host + '/common/' + req.body.appId + '/User_Info/currentUserInfo',
      headers: {
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
      },
      json: true
    };
    var platform_data = {
      method: 'GET',
      url: req.body.propertyList.platform_url + '/spring/smartapp/currentUser',
      headers: {
        Authorization: req.headers.authorization
      },
      json: true
    };
    function get_collection() {
      return new Promise(function(resolve, reject) {
        request(connect, function(err, response, body) {
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }
    function get_platform_data() {
      return new Promise(function(resolve, reject) {
        request(platform_data, function(err, response, body) {
          if (err || body.error) reject(body);
          else {
            resolve(body);
          }
        });
      });
    }
    Promise.all([get_collection(), get_platform_data()])
      .then(function(result) {
        let collection = result[0];
        let platform_data = result[1];
        if (collection.length > 0) {
          if (collection[0].username === platform_data.data.emailAddress) {
            collection['username'] = platform_data.data.emailAddress;
            collection['address'] = platform_data.data.address[0].street;
            collection = JSON.parse(
              JSON.stringify(collection)
                .split('"emailAddress":')
                .join('"email":')
            );
            collection = JSON.parse(
              JSON.stringify(collection)
                .split('"mobileno":')
                .join('"mobile":')
            );
            collection = JSON.parse(
              JSON.stringify(collection)
                .split('"phoneCountryCode":')
                .join('"phCountryCode":')
            );
            collection = JSON.parse(
              JSON.stringify(collection)
                .split('"phoneno":')
                .join('"phone":')
            );
            collection.push({ ...collection[0], ...platform_data });
          }
        } else {
          collection.push({ ...platform_data.data });
          collection['username'] = platform_data.data.emailAddress;
          collection['address'] = platform_data.data.address[0].street;
          collection = JSON.parse(
            JSON.stringify(collection)
              .split('"emailAddress":')
              .join('"email":')
          );
          collection = JSON.parse(
            JSON.stringify(collection)
              .split('"mobileno":')
              .join('"mobile":')
          );
          collection = JSON.parse(
            JSON.stringify(collection)
              .split('"phoneCountryCode":')
              .join('"phCountryCode":')
          );
          collection = JSON.parse(
            JSON.stringify(collection)
              .split('"phoneno":')
              .join('"phone":')
          );
        }
         success(collection,200);
      })
      .catch(err => {
        console.log('Error->' + err);
  error(err,500);      
      });
