let propertyList = req.body.propertyList;
var output = [];
var properties = [];
if (propertyList.isAWSUser) output.push({ target: 'Property', targetType: 'isAWSUser', status: 'Success' });
else
    output.push({
        target: 'Property',
        targetType: 'isAWSUser',
        status: 'Fail',
        statusMessage: 'isAWSUser property not present'
    });
var collection = {
    method: 'POST',
    url: propertyList.platform_url + '/collection/v1?',
    headers: {
        Authorization: req.headers.authorization,
        'Content-Type': 'application/json',
        ttl: 300,
        'X-TenantID': req.headers['x-tenantid'],
        'X-Remote-User': 'ekaApp'
    },
    json: true
};

function get_collection(collectionName) {
    if (collectionName) {
        collection.url =
            req.body.propertyList.eka_connect_host +
            '/collectionmapper/' +
            req.body.appId +
            '/9c98c493-ee2f-490d-be92-fe2424f513e5/fetchCollectionRecords';
        collection.body = {};
        collection.body = {
            collectionName: collectionName
        };
    }
    return new Promise(function (resolve, reject) {
        request(collection, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}
var property = {
    method: 'GET',
    url: '',
    json: true
};
function get_property(propertyURL) {
    if (propertyURL) {
        property.url = propertyURL;
    }
    return new Promise(function (resolve, reject) {
        request(property, function (err, response, body) {
            if (response && response.statusCode) {
                body.status = '';
                body.status = response.statusCode;
            }
            resolve(err || body);
        });
    });
}

var collectionName = [
    'Realtime PBS Supplier Details',
    'Realtime PBS Customer Details',
    'Realtime PBS Loading Discharge Details',
    'Sampling Physical trade details',
    'voyage_collection',
    'voyage_loadingport',
    'voyage_dischargeport',
    'freight_collection',
    'general_collection',
    'seller_collection',
    'selleroptionality_collection',
    'buyer_collection',
    'buyeroptionality_collection',
    'fca_collection',
    'owner_collection',
    'loi_collection'
];

//Properties
properties = [
    {
        propertyName: 'eka_connect_ui_host',
        propertyURL: propertyList.eka_connect_ui_host
    },
    { propertyName: 'eka_connect_host', propertyURL: propertyList.eka_connect_host },
    { propertyName: 'eka_utility_host', propertyURL: propertyList.eka_utility_host },
    { propertyName: 'eka_ctrm_host', propertyURL: propertyList.eka_ctrm_host }
];
const run = async function () {
    const collectionData = await Promise.all(
        collectionName.map(async item => {
            return await get_collection(item);
        })
    ).then(result => {
        function newItem(item, index) {
            if (item) output.push({ target: 'Collection', targetType: collectionName[index], status: 'Success' });
            else
                output.push({
                    target: 'Collection',
                    targetType: collectionName[index],
                    status: 'Fail',
                    statusMessage: item['message']
                });
        }
        result.forEach(newItem);
        return output;
    });
    const errorResponse = [];
    const propertyData = await Promise.all(
        properties.map(async item => {
            return await get_property(item.propertyURL);
        })
    ).then(err => {
        function error(item, index) {
            if (Number(item.status) >= 400 && Number(item.status) <= 600)
                errorResponse.push({ target: 'API', targetType: properties[index]['propertyName'], status: 'Success' });
            else
                errorResponse.push({
                    target: 'API',
                    targetType: properties[index]['propertyName'],
                    status: 'Fail',
                    statusMessage: properties[index]['propertyURL'] + ' is failing. PLease check'
                });
        }
        err.forEach(error);
        return errorResponse;
    });
    collectionData;
    propertyData;
    errorResponse;
    var responseArray = [];
    responseArray = collectionData.concat(propertyData);
    res.status(200).send(responseArray);
};
run();
