var sessionStorageData = req.body.queryParams;
var plannedBulkShipmentNo = sessionStorageData.plannedBulkShipmentNo;
plannedBulkShipmentNo = plannedBulkShipmentNo.toString();
var filterarr = [];
filterarr.push({
    fieldName: 'Planned Bulk Shipment Ref No',
    value: plannedBulkShipmentNo,
    operator: 'in'
});
filterarr.push({
    fieldName: 'Item Type',
    value: 'Sales Contract',
    operator: 'in'
});
var salesContractItemBody = {};
salesContractItemBody = {
    criteria: {
        filter: filterarr
    },
    collectionName: req.body.propertyList.sellerBuyer_collection_name
};
var salesContractItem = {
    method: 'POST',
    body: salesContractItemBody,
    url:
        req.body.propertyList.eka_connect_host +
        '/collectionmapper/' +
        req.body.appId +
        '/9c98c493-ee2f-490d-be92-fe2424f513e5/fetchCollectionRecords',
    headers: {
        Authorization: req.headers.authorization,
        'Content-Type': 'application/json',
        ttl: 300,
        'X-TenantID': req.headers['x-tenantid'],
        'X-Remote-User': 'ekaApp'

    },
    json: true
};
function get_salesContractItem() {
    return new Promise(function (resolve, reject) {
        console.log(salesContractItem.body);
        request(salesContractItem, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}
var connectUrl = req.body.propertyList.eka_connect_host + '/workflow/data';
var connectBody = {
    appId: req.body.appId,
    workFlowTask: 'createbuyerlistCOPY'
};
var connect = {
    method: 'POST',
    body: connectBody,
    url: connectUrl,
    headers: {
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
    },
    json: true
};
if (req.headers.hasOwnProperty('device-id')) {
    connect.headers['Device-Id'] = req.headers['device-id'];
}
function get_connect() {
    return new Promise(function (resolve, reject) {
        request(connect, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}
Promise.all([get_salesContractItem(), get_connect()])
    .then(result => {
        var buyer_collection = result[0];
        var connectData = result[1].data;
        var collectiondata = [];
        for (var i = 0; i < buyer_collection.length; i++) {
            buyer_collection[i]['CP Name'] = buyer_collection[i]['CP Name'].trim();
            if (buyer_collection[i]['Planned Bulk Shipment Ref No'] === req.body.queryParams.plannedBulkShipmentNo[0]) {
                collectiondata.push({
                    buyer: buyer_collection[i]['CP Name'],
                    buyer_id: buyer_collection[i]['CP Name'],
                    plannedBulkShipmentNo: req.body.queryParams.plannedBulkShipmentNo[0],
                    buyerpbs: req.body.queryParams.plannedBulkShipmentNo[0] + buyer_collection[i]['CP Name']
                });
            }
        }
        if (connectData.length > 0) {
            for (var k = 0; k < collectiondata.length; k++) {
                for (var j = 0; j < connectData.length; j++) {
                    if (connectData[j]['buyerpbs'] === collectiondata[k]['buyerpbs'])
                        collectiondata[k] = {
                            buyer: collectiondata[k]['buyer'],
                            buyer_id: collectiondata[k]['buyer'],
                            plannedBulkShipmentNo: req.body.queryParams.plannedBulkShipmentNo[0],
                            buyerpbs: req.body.queryParams.plannedBulkShipmentNo[0] + collectiondata[k]['buyer'],
                            ...connectData[j]
                        };
                }
            }
        }
        collectiondata = collectiondata.reduce((unique, o) => {
            if (!unique.some(obj => obj.buyerpbs === o.buyerpbs)) {
                unique.push(o);
            }
            return unique;
        }, []);
        res.status(200).send(collectiondata);
    })
    .catch(err => {
        res.status(500).send(err);
    });
