const appId = '12325a98-a959-4939-9005-4158d136afcd';
const pricesObjId = 'ab3fc628-ecbc-43e0-81cb-aa2d5b6e5c40';
const today = moment(moment().format('YYYY-MM-DD HH:mm'), 'YYYY-MM-DD HH:mm');
const tenant = req.headers['x-tenantid'];
const serverTimezoneOffset = new Date().getTimezoneOffset();
const taskId = 'pricelimitsdomainquery';

const getObjectUri = id => `${req.body.propertyList.eka_connect_host}/data/${appId}/${id}`;
const getData = (url, getDataBody) =>
  new Promise(function(resolve, reject) {
    request(
      {
        method: 'GET',
        url,
        headers: {
          Authorization: req.headers.authorization,
          'X-TenantID': tenant
        },
        json: true,
        body: getDataBody
      },
      function(err, response, body) {
        if (response && response.statusCode >= 200 && response.statusCode <= 299) {
          resolve(body);
        } else {
          reject(body);
        }
      }
    );
  });

const priceStatus = price => {
  const start = moment(price.startDate + ' ' + price.startTime, 'YYYY-MM-DD HH:mm');
  const end = moment(price.endDate + ' ' + price.endTime, 'YYYY-MM-DD HH:mm');
  const offset = price.clientTimezoneOffset ? price.clientTimezoneOffset - serverTimezoneOffset : 0;
  start.add(offset, 'minutes');
  end.add(offset, 'minutes');

  let status = price.status || '';
  if (status != 'Expired') {
    if (moment(start).isValid() && moment(end).isValid()) {
      if (moment(end).isSameOrAfter(today) && moment(start).isSameOrBefore(today)) {
        status = 'Active';
      }
      if (moment(start).isAfter(today) && moment(end).isAfter(today)) {
        status = 'Scheduled';
      }
      if (moment(start).isBefore(today) && moment(end).isBefore(today)) {
        status = 'Expired';
      }
    }
  }

  return status;
};

const getPrices = bpList => {
  return new Promise((resolve, reject) => {
    var body = {
      filterData: {
        filter: [
          {
            fieldName: 'bpRefNo',
            operator: 'in',
            value: bpList.map(e => e.bpRefNo)
          },
          {
            "fieldName": "endDateTime",
            "operator": "gte",
            "value": today.utc().format('YYYY-MM-DDT00:00:00Z'),
          }
        ]
      }
    };
    if (bpList[0].bpExternalCode) {
      body.filterData.filter.push({
        fieldName: 'bpExternalCode',
        operator: 'inCaseInsensitive',
        value: bpList.map(e => e.bpExternalCode)
      });
    }
    getData(getObjectUri(pricesObjId), body)
      .then(price => {
        console.time();
        resolve(
          price.map(i => ({
            ...i,
            status: priceStatus(i),
            type: 'price',
            quantityUnit: 'MT',
            purchased: i.purchased ? i.purchased : 0
          }))
        );
        console.timeEnd();
      })
      .catch(err => {
        reject('Error when fetching price');
      });
  });
};
const userInfo = req.body.propertyList.eka_connect_host + '/api/getbpuserinfo';
const getCollectionMapper = config =>
  new Promise((resolve, reject) => {
    const r = {
      method: 'POST',
      url: `${req.body.propertyList.eka_connect_host}/collectionmapper/${config.appId}/${
        config.objectId
      }/fetchCollectionRecords`,
      headers: {
        Authorization: req.headers.authorization,
        ttl: 600,
        'X-TenantID': config.tenant,
        'Content-Type': 'application/json'
      },
      body: {
        collectionName: config.collectionName
      },
      json: true
    };
    request(r, (err, response, body) => {
      if (response && response.statusCode >= 200 && response.statusCode <= 299) {
        resolve(body);
      } else {
        reject(body);
      }
    });
  });
const subGroupMaster = {
  appId: appId,
  objectId: 'd59b8c10-8c94-4b87-a430-f6fd69888a37' /* not at all relevant object id */,
  collectionName: 'BP Sub Group Setup',
  tenant: tenant
};
const subGroupCode = userDetails =>
  new Promise((resolve, reject) =>
    getCollectionMapper(subGroupMaster)
      .then(result => {
        if (result && Array.isArray(result)) {
          const config = result.find(i => i.Workflow == taskId && i.UserType == userDetails.customerType);

          if (config) {
            resolve(config.SubGroupCode);
          } else {
            console.log(
              `Sub group configuration not found for ${taskId} and ${
                userDetails.customerType
              }. Defaulting to not applicable.`
            );
            resolve(null);
          }
        } else {
          reject('Sub group configuration is missing');
        }
      })
      .catch(err => {
        reject(err);
      })
  );

const bpList = new Promise((resolve, reject) => {
  getData(userInfo, null)
    .then(t => {
      const userDetails = t.data;
      if (userDetails.BusinessPartyDetails) {
        subGroupCode(userDetails)
          .then(code => {
            let bpResolver;
            if (code) {
              // bpResolver = (masterdata) =>
              bpResolver = userDetails.BusinessPartyDetails
                // .filter((bp) =>masterdata.includes(bp.refNo)
                // )
                .reduce((acc, bp) => {
                  if (bp.subGroup && bp.subGroup.length > 0) {
                    const subGroup = bp.subGroup.filter(i => i.code && i.code.toLowerCase() == code.toLowerCase());
                    if (subGroup && subGroup.length > 0) {
                      for (let n in subGroup) {
                        acc.push({
                          bpRefNo: `${bp.refNo}`, // @#@ ${bp.name} @#@ ${subGroup.name} @#@ ${subGroup.refNo}`,
                          bpExternalCode: `${subGroup[n].refNo}` //``${bp.name} - ${bp.refNo} - ${subGroup.name}`,
                        });
                      }
                    }
                  }
                  return acc;
                }, []);
            } else {
              // bpResolver = (masterdata) =>
              bpResolver = userDetails.BusinessPartyDetails
                // .filter((bp) =>
                //   masterdata.includes(bp.refNo)
                // )
                .map(bp => ({
                  bpRefNo: `${bp.refNo}` // @#@ ${bp.name}`,
                }));
            }
            // console.log(bpResolver(''));
            resolve(bpResolver);
          })
          .catch(err => {
            reject(err);
          });
      } else {
        reject('BusinessPartyDetails missing in user details.');
      }
    })
    .catch(err => {
      reject(err);
    });
});
bpList
  .then(bpListResponse => {
    getPrices(bpListResponse)
      .then(function(data) {
        let prices = data.filter(i => i.status == 'Active' || i.status == 'Expired');
        res.status(200).send([...prices]);
      })
      .catch(function(err) {
        console.log(err);
        return res.status(500).send(err);
      });
  })
  .catch(function(err) {
    console.log(err);
    return res.status(500).send(err);
  });
