    if (
      req &&
      req.body &&
      req.body.payLoadData &&
      req.body.payLoadData.qualitylist &&
      req.body.payLoadData.qualitylist.product_specs
    ) {
      var productName = req.body.payLoadData.qualitylist.product_specs;
      var productURl = req.body.propertyList.platform_url + '/mdmapi/data';
      var productAttributesURL = req.body.propertyList.platform_url + '/mdmapi/product/attributes';
      var productBody = [
        {
          dropDownType: 'combo',
          limit: '9999',
          serviceKey: 'productComboDropDrown'
        }
      ];
      var product = {
        method: 'POST',
        url: productURl,
        body: productBody,
        headers: {
          Authorization: req.headers.authorization,
          'Content-Type': 'application/json'
        },
        json: true
      };
      var productAttributes = {
        method: 'POST',
        url: productAttributesURL,
        headers: {
          Authorization: req.headers.authorization,
          'Content-Type': 'application/json',
          'X-TenantID': req.headers['x-tenantid']
        },
        json: true
      };
      function get_productDetails() {
        return new Promise(function(resolve, reject) {
          request(product, function(err, response, body) {
            if (err || body.error) reject(body);
            else {
              resolve(body);
            }
          });
        });
      }
      function get_productAttributes(productid) {
        productAttributes.body = {};
        productAttributes.body = {
          productId: productid,
          isUsedForClassification1: 'Y',
          isUsedForPd1: 'Y'
        };
        return new Promise(function(resolve, reject) {
          request(productAttributes, function(err, response, body) {
            if (err || body.error) reject(body);
            else {
              resolve(body);
            }
          });
        });
      }
      var productId;
      get_productDetails()
        .then(function(result) {
          var productData = result.productComboDropDrown;

          for (var i = 0; i < productData.length; i++) {
            if (productData[i]['value'] == productName) {
              productId = productData[i]['key'];
              break;
            }
          }
          get_productAttributes(productId)
            .then(function(result1) {
              var attributes = result1;
              var x = req.body;
              if (attributes.length == 0) {
                res.status(500).send('Quality attributes mandatory, please do the required configuration to proceed');
              } else {
                for (var i = 0; i < attributes.length; i++) {
                  var type;
                  var dataType;
                  var serviceKey;
                  var dropdownValue;
                  if (attributes[i]['attributeValueTypeId'] == 'PREDEFINEDLIST') {
                    type = 'dropdown';
                    dataType = 'String';
                    serviceKey = attributes[i]['propertyId'];
                    dropdownValue = attributes[i]['attributeName'] + 'DisplayName';
                  }
                  if (attributes[i]['attributeValueTypeId'] == 'TEXT') {
                    type = 'textbox';
                    dataType = 'String';
                  }
                  if (attributes[i]['attributeValueTypeId'] == 'NUMBER') {
                    type = 'textbox';
                    dataType = 'Number';
                  }
                  req.body.objectMeta.fields[attributes[i]['attributeName']] = {};
                  if (type == 'dropdown') {
                    req.body.objectMeta.fields[attributes[i]['attributeName']] = {
                      [attributes[i]['attributeName']]: attributes[i]['attributeName'],
                      labelKey: attributes[i]['attributeName'],
                      type: type,
                      isRequired: false,
                      dataType: dataType,
                      serviceKey: serviceKey,
                      dropdownValue: dropdownValue
                    };
                    req.body.objectMeta.fields[dropdownValue] = {};
                    req.body.objectMeta.fields[dropdownValue] = {
                      [dropdownValue]: dropdownValue,
                      labelKey: dropdownValue,
                      type: 'textbox',
                      dataType: 'String'
                    };
                    req.body.flow.editcreatequalitydynamic.fields.push({
                      filter: true,
                      edit: true,
                      sort: true,
                      key: dropdownValue,
                      hidden: true
                    });
                  } else {
                    req.body.objectMeta.fields[attributes[i]['attributeName']] = {
                      [attributes[i]['attributeName']]: attributes[i]['attributeName'],
                      labelKey: attributes[i]['attributeName'],
                      type: type,
                      isRequired: false,
                      dataType: dataType
                    };
                  }
                  req.body.flow.editcreatequalitydynamic.fields.push({
                    filter: true,
                    edit: true,
                    sort: true,
                    key: attributes[i]['attributeName']
                  });
                }
              }
              var output = {
                appId: req.body.appId,
                flow: req.body.flow,
                object: req.body.object,
                objectMeta: req.body.objectMeta,
                properties: req.body.properties
              };
              res.status(200).send(output);
            })
            .catch(err => {
              console.log(err);
            });
        })
        .catch(err => {
          console.log(err);
        });
    } else {
      res.status(200).send({appId: req.body.appId,
                flow: req.body.flow,
                object: req.body.object,
                objectMeta: req.body.objectMeta,
                properties: req.body.properties});
    }
