 var gmrRefNo = req.body.selectedData.gmrRefNo;
var platform = {
  method: "POST",
  url: req.body.propertyList.eka_connect_host + "/workflow",
  headers: {
    "Content-Type": "application/json",
    Authorization: req.headers.authorization,
    "X-TenantID": req.headers["x-tenantid"]
  },
  body: {
    appId: "467a28cc-bc93-4e38-8ff5-0a56ae128f3b",
    workflowTaskName: "viewdoclistdata_SC",
    task: "viewdoclistdata_SC",
    payLoadData: "",
    output: {
      viewdoclistdata_SC: {
    otherAttributes: {
      internalRefNo: internalRefNo,
    },
  },
    },
  },
   json: true
};
function get_platform() {
  return new Promise(function(resolve, reject) {
    console.log("Request - " + platform.url);
    console.log("Payload -" + JSON.stringify(platform));
    request(platform, function(err, response, body) {
      console.log("Request Body - " + JSON.stringify(body));
      if (err || body.error) reject(body);
      else {
        resolve(body);
      }
    });
  });
}

