var result = {};
    result.data = req.body.bulkPayLoadData;

    for (var i = 0; i < result.data.length; i++) {
      if (result.data[i].gmrRefNo) {
        result.data[i].gmrRefNo_hidden = parseInt(result.data[i].gmrRefNo.split("-")[1]);
        // result.data[i].gmrRefNo_hidden = result.data[i].gmrRefNo.split("-")[1];
      }

      if (result.data[i].contractDeliveryRefNo) {
        let item = result.data[i].contractDeliveryRefNo.split("-");
        item[3] = parseInt(item[3].toString().replace(".", ""), 10);
        result.data[i].deliveryItemRefNo_hidden = parseFloat(item[1] + '.' + item[3]);
      }

      if (result.data[i].invoiceAmount) {
        let no1 = parseInt(result.data[i].invoiceAmount.split(",").join(''));
        let no2 = result.data[i].invoiceAmount.split(".");
        result.data[i].invoiceAmount_hidden = parseFloat(no1 + '.' + no2[1]);
      }

      if (result.data[i].invoicedWeight) {
        let no1 = parseInt(result.data[i].invoicedWeight.split(",").join(''));
        let no2 = result.data[i].invoicedWeight.split(".");
        result.data[i].invoicedWeight_hidden = parseFloat(no1 + '.' + no2[1]);
		if(!no1){
			result.data[i].invoicedWeight = parseFloat('.' + no2[1]);
		}else{
			result.data[i].invoicedWeight = parseFloat(no1+ '.' + no2[1]);
		}
      }
      
    }
    console.log("Response length - " + result.data.length);
    res.status(200).send(result);
