req.body.mdmProcessorServiceKey = JSON.parse(req.body.mdmProcessorServiceKey);
var serviceKey = req.body.mdmProcessorServiceKey[0].serviceKey;
var ekaEntity =req.body.propertyList.platform_url + '/spring/cashManagement/entity';

var platformEntityTransactionData = {
  method: 'GET',
  url: ekaEntity,
  headers: {
    Authorization: req.headers.authorization,
    'Content-Type': 'application/json',
    ttl: 300,
    'X-TenantID': req.headers['x-tenantid'],
    'X-Remote-User': 'ekaApp'
  },
  json: true
};
var get_platformEntityTransactionData = function get_platformEntityTransactionData() {
  return new Promise(function(resolve, reject) {
    request(platformEntityTransactionData, function(err, response, body) {
      if (response.statusCode >= 200 && response.statusCode <= 299) {
        resolve(body);
      } else {
        reject(body);
      }
    });
  });
};

get_platformEntityTransactionData()
  .then(function(result) {
    var mdmData = [];
    var data = {};
    result.forEach(function(res) {
      mdmData.push({ key: res['c1'], value: res['c1'] });
    });
    data[serviceKey] = mdmData;
    res.status(200).send(data);
  })
  .catch(function(err) {
    return res.status(500).send(err);
  });
