const formElementName = 'pricelimits';
let data = req.body.bulkPayLoadData || [];
const serverTimezoneOffset = new Date().getTimezoneOffset();
const today = moment(moment().format('YYYY-MM-DD HH:mm'), 'YYYY-MM-DD HH:mm');
const parentMsg = 'Invalid start date/time. Please review your entry';
const childMsg = 'Invalid start date/time. Please review your entry';
data.filter(o=>o.status!='Expired').forEach(i => {
  i.startTime= i.formattedPublish
    ? moment()
        .add(
          i.clientTimezoneOffset
            ? serverTimezoneOffset - i.clientTimezoneOffset
            : 0,
          'minutes'
        )
        .format('HH:mm')
    : i.startTime;
  i.startDate = i.formattedPublish
    ? moment()
        .add(
          i.clientTimezoneOffset
            ? serverTimezoneOffset - i.clientTimezoneOffset
            : 0,
          'minutes'
        )
        .format('YYYY-MM-DD')
    : i.startDate;
  i.startDateTime= i.formattedPublish
    ? moment().utc().format('YYYY-MM-DDTHH:mm:ss[Z]')
    : i.startDateTime;
});

const errorWithDates = data.filter((i) =>
  today.isAfter(
    moment(i.startDate + ' ' + i.startTime, 'YYYY-MM-DD HH:mm:ss[Z]').add(
      i.clientTimezoneOffset
        ?  i.clientTimezoneOffset - serverTimezoneOffset
        : 0,
      'minutes'
    )
  ) && i.status != 'Expired'
);
if (errorWithDates && errorWithDates.length > 0) {
  res.status(400).send({
    errorCode: '004',
    errorMessage: parentMsg,
    errorContext: null,
    errorLocalizedMessage: parentMsg,
    errors: [
      {
        errorCode: '004',
        errorMessage: parentMsg,
        errorContext: `{formarray:${formElementName}}`,
        errorLocalizedMessage: parentMsg,
        errors: errorWithDates.map((i) => ({
          errorCode: '004',
          errorMessage: childMsg,
          errorContext: `{formarray:${i.index}}`,
          errorLocalizedMessage: childMsg,
          errors: [
            {
              errorCode: '004',
              errorMessage: childMsg,
              errorContext: `{field:formattedStartTime}`,
              errorLocalizedMessage: childMsg,
              errors: null,
            },
          ],
        })),
      },
    ],
  });
} else {
  res.status(200).send(data);
}
