var selectedData = req.body.bulkPayLoadData[0];
var platformurl = req.body.propertyList.platform_url + '/api/contract/warrantContract/modify';
var platformapi = {
    method: 'POST',
    url: platformurl,
    headers: {
        Authorization: req.headers.authorization
    },
    json: true
};
function get_platform() {
    let outputData = {
        contractRefNo: selectedData.contract_ref_no + ',1',
        contractPrice: selectedData.futurePrice.toString(),
        modificationDate: selectedData.activityDate,
        reasonForModify: selectedData.warrant_remarks
    };
    platformapi.body = outputData;
    console.log(platformapi);
    return new Promise(function (resolve, reject) {
        request(platformapi, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}
