var selectedData = req.body.bulkPayLoadData;
var platformurl = req.body.propertyList.platform_url + '/api/contract/warrantContract/cancel';
if (selectedData.length <= 0) {
    res.status(500).send('Error in selected Data');
}
var platformBody = {
    contractRefNo: selectedData[0].contract_ref_no,
    cancellationDate: selectedData[0].activityDate,
    remarks: selectedData[0].reasons_for_cancellation
};
var platformapi = {
    method: 'POST',
    url: platformurl,
    body: platformBody,
    headers: {
        Authorization: req.headers.authorization
    },
    json: true
};
console.log(platformapi);
function get_platform() {
    return new Promise(function (resolve, reject) {
        request(platformapi, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}

get_platform()
    .then(function (result) {
        console.log(result);
        if (result.status == 'success') {
            var output = [];
            for (let i = 0; i < selectedData.length; i++) {
                output.push({
                    contract_ref_no: result.externalRefNo,
                    contract_status: 'Rejected',
                    ext_stock_ref_no: selectedData[i].ext_stock_ref_no,
                    edmarkrefno: '1'
                });
            }
            res.status(200).send(output);
        } else {
            if (result.description) {
                res.status(500).send('Error in Purchase contract cancellation:(' + result.description + ')');
            } else {
                res.status(500).send('Purchase contract cancellation is failed, please try once again');
            }
            return;
        }
    })
    .catch(err => {
        console.log('Error: ' + err);
        res.status(500).send(err);
    });
