let data = req.body.bulkPayLoadData;
var responseData = [];
for (var i = 0; i < data.length; i++) {
  if (data[i]['status'] == true) {
    delete data[i]['_id'];
    if (data[i]['operations'] == 'De-certify') {
      if (data[i]['certification_status'] == 'Certified') {
        data[i]['certification_status'] = 'Not-Certified';
        responseData.push(data[i]);
      }
    } else if (data[i]['operations'] == 'Undo-Decertify') {
      if (data[i]['certification_status'] == 'Not-Certified') {
        data[i]['certification_status'] = 'Certified';
        responseData.push(data[i]);
      }
    } else if (
      data[i]['operations'] == 'Marking for Delivery' ||
      data[i]['operations'] == 'Un-Marking for Delivery'
    ) {
      responseData.push(data[i]);
    } else {
      if (data[i]['grading_status'] == 'Passed') {
        data[i]['grading_status'] = 'Re-grading';
        responseData.push(data[i]);
      } else if (data[i]['grading_status'] == 'Failed') {
        data[i]['grading_status'] = 'Appeal';
        responseData.push(data[i]);
      } else if (data[i]['grading_status'] == 'Pending') {
        data[i]['grading_status'] = 'Ungraded';
        responseData.push(data[i]);
      } else if (data[i]['grading_status'] == 'Appeal') {
        data[i]['grading_status'] = 'Failed';
        responseData.push(data[i]);
      } else if (data[i]['grading_status'] == 'Re-grading') {
        data[i]['grading_status'] = 'Passed';
        responseData.push(data[i]);
      }
    }
  }
}
res.status(200).send(responseData);
