var connectUrl = req.body.propertyList.eka_connect_host + '/workflow/data';
var connectBody = {
    appId: req.body.appId,
    workFlowTask: 'dummycontractlist'
};
var connect = {
    method: 'POST',
    body: connectBody,
    url: connectUrl,
    headers: {
        Authorization: req.headers.authorization,
        'X-TenantID': req.headers['x-tenantid']
    },
    json: true
};
function get_connect() {
    return new Promise(function (resolve, reject) {
        request(connect, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}
function get_stoppingList() {
    connect.body = {
        appId: req.body.appId,
        workFlowTask: 'dummystoppinglist'
    };
    return new Promise(function (resolve, reject) {
        request(connect, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}
Promise.all([get_connect(), get_stoppingList()]).then(function (result) {
    let contractlist = result[0].data;
    let stoppinglist = result[1].data;
    let selectedData = req.body;
    var outputArr = [];
    var outputData = [];
    if (selectedData.purchase_contract_ref_no) {
        selectedData['contract_ref_no'] = selectedData.purchase_contract_ref_no;
    }
    for (let j = 0; j < contractlist.length; j++) {
        if (selectedData['contract_ref_no'] == contractlist[j]['contract_ref_no']) {
            outputArr.push(contractlist[j]);
        }
    }
    for (let i = 0; i < stoppinglist.length; i++) {
        for (let j = 0; j < outputArr.length; j++) {
            if (
                outputArr[j]['ext_stock_ref_no'] == stoppinglist[i]['ext_stock_ref_no'] ||
                (outputArr[j]['ext_stock_ref_no_array'] &&
                    outputArr[j]['ext_stock_ref_no_array'].includes(stoppinglist[i]['ext_stock_ref_no']))
            ) {
                outputData.push({
                    ...outputArr[j],
                    ...stoppinglist[i],
                    contract_ref_no: outputArr[j]['contract_ref_no'],
                    internalContractItemRefNo: outputArr[j]['internalContractItemRefNo'],
                    contract_creation_date: outputArr[j]['contract_creation_date']
                });
            }
        }
    }
    outputData = outputData.reduce((unique, o) => {
        if (!unique.some(obj => obj.ext_stock_ref_no === o.ext_stock_ref_no)) {
            unique.push(o);
        }
        return unique;
    }, []);
    for (let i = 0; i < outputData.length; i++) {
        delete outputData[i]['_id'];
        delete outputData[i]['object'];
        delete outputData[i]['refTypeId'];
        if (outputData[i]['sys__createdBy']) delete outputData[i]['sys__createdBy'];
        if (outputData[i]['sys__createdOn']) delete outputData[i]['sys__createdOn'];
        if (outputData[i]['sys__data__state']) delete outputData[i]['sys__data__state'];
        if (outputData[i]['sys__updatedBy']) delete outputData[i]['sys__updatedBy'];
        if (outputData[i]['sys__updatedOn']) delete outputData[i]['sys__updatedOn'];
        if (outputData[i]['sys__UUID']) delete outputData[i]['sys__UUID'];
        if (outputData[i]['sys__version']) delete outputData[i]['sys__version'];
        if (outputData[i]['userId']) delete outputData[i]['userId'];
        outputData[i]['no_Of_Units'] = '';
        outputData[i]['no_Of_Units'] = selectedData.no_Of_Units;
        outputData[i]['gmr_creation_date'] = '';
        outputData[i]['gmr_creation_date'] = selectedData.activityDate;
    }
    res.status(200).send(outputData);
});
