let selectedData = req.body.selectedData;
var instrumentName = selectedData[0]['instrument_name'];
var url = req.body.propertyList.platform_url + '/mdmapi/masterdatas/instrumentPriceDetail/' + instrumentName;
var api = {
    method: 'POST',
    url: url,
    headers: {
        Authorization: req.headers.authorization
    },
    json: true
};
function get_data(instrument_name_value) {
    return new Promise(function (resolve, reject) {
        request(api, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}
get_data().then(function (result) {
    var data = result;
    var exchange_lots = 0;
    var quantity = 0;
    var ext_stock_ref_no = [];
    var stoppingstocks = req.body.selectedData.stoppingmarketstockslist;
    for (var i = 0; i < stoppingstocks.length; i++) {
        if (stoppingstocks[i]['exchange_lots']) {
            exchange_lots = exchange_lots + stoppingstocks[i]['exchange_lots'];
        }
        if (stoppingstocks[i]['quantity']) {
            stoppingstocks[i]['quantity'] = parseFloat(stoppingstocks[i]['quantity']);
            quantity = quantity + stoppingstocks[i]['quantity'];
        }
        if (stoppingstocks[i]['ext_stock_ref_no']) {
            ext_stock_ref_no.push(stoppingstocks[i]['ext_stock_ref_no']);
        }
    }
    var output = {
        futurePriceUnit: [{ key: data['priceUnitName'], value: data['priceUnitName'] }],
        no_of_lots: exchange_lots,
        quantity: quantity.toFixed(2)
    };
    res.status(200).send(output);
