let selectedData = req.body;
var outputData = {
    activityDate: selectedData.activityDate,
    internalRemarks: selectedData.reasons_for_cancellation,
    gmrRefNo: selectedData.GMR_Ref_No
};
var platformurl = req.body.propertyList.platform_url + '/api/logisticinternal/warrantSalesLoading/cancel';
var platformapi = {
    method: 'POST',
    body: outputData,
    url: platformurl,
    headers: {
        Authorization: req.headers.authorization
    },
    json: true
};
console.log(platformapi);
function get_platform() {
    return new Promise(function (resolve, reject) {
        request(platformapi, function (err, response, body) {
            if (response.statusCode >= 200 && response.statusCode <= 299) {
                resolve(body);
            }
            else {
                reject(body);
            }
        });
    });
}
get_platform()
    .then(function (result) {
        console.log(result);
        if (result.status == 'success') {
            var output = {};
            output = {
                contract_ref_no: selectedData.Contract_Ref_No,
                GMR_Ref_No: '',
                gmr_creation_date: '',
                title_transfer_status: 'In',
                GMR_creation_date: ''
            };

            res.status(200).send(output);
        } else {
            if (result.description) {
                res.status(500).send('Error in Sales execution cancellation:(' + result.description + ')');
            } else {
                res.status(500).send('Sales execution cancellation has failed, please try once again');
            }
        }
    })
    .catch(err => {
        res.status(500).send('Sales execution cancellation has failed, please try once again');
    });
