var moment = require('moment');
let mdmbody = JSON.parse(req.body.mdmProcessorServiceKey);
req.body.mdmProcessorServiceKey = JSON.parse(req.body.mdmProcessorServiceKey);
let limitCurrency = req.body.selectedData.generaldetails.limitCurrency;
function findindex(serviceKey) {
  return req.body.mdmProcessorServiceKey.findIndex(
    (x) => x.serviceKey == serviceKey
  );
}
var TemplateUrl =
  req.body.propertyList.platform_url + "/collection/v1/internal/template/";
var templateBody = {
  method: "GET",
  url: TemplateUrl,
  headers: {
    Authorization: req.headers.authorization,
    "Content-Type": "application/json",
    ttl: 300,
    "X-TenantID": req.headers["x-tenantid"],
    "X-Remote-User": "ekaApp",
  },
  json: true,
};
var get_template = function get_template(name) {
  templateBody.url =
    req.body.propertyList.platform_url +
    "/collection/v1/internal/template/" +
    name + '?start=0&limit=10000';
  return new Promise(function (resolve, reject) {
    request(templateBody, function (err, response, body) {
      if (response.statusCode >= 200 && response.statusCode <= 299) {
        resolve(body);
      } else {
        reject(body);
      }
    });
  });
};
async function main() {
  let mdmdata = {};
  if (findindex("currency") != -1) {
    try {
      let start = new Date();
      let BankDetails = await get_template("TreasuryBankAccount");
      mdmdata["currency"] = [
        ...new Set(BankDetails.data.map((obj) => obj["Currency"])),
      ].map((item) => {
        return { key: item, value: item };
      });
    } catch (error) {
      console.log(error);
    }
  }
  if (findindex("currencyNotion") != -1) {
    mdmdata["currencyNotion"] = [
        { key: limitCurrency, value: limitCurrency }
      ]
}

  return mdmdata;
}
